mapper.select:
select * from (
	select rownum rnum, a.* from (
		select * from petboard 
			where boardid = #{boardid} and postDisable = 0
			order by postId desc
	) a 
) where rnum between #{start} and #{end}

select * from (
	select rownum rnum, a.* from (
		select * from petboard 
			where boardid = #{boardid} and postDisable = 0 and petType = 0
			order by postId desc
	) a 
) where rnum between #{start} and #{end}

select * from (
	select rownum rnum, a.* from (
		select * from petboard 
			where boardid = #{boardid} and postDisable = 0 and petType = 1
			order by postId desc
	) a 
) where rnum between #{start} and #{end}
